 all_sig &lt;- ufc |&gt;
  filter(FightYear &lt; 2014) |&gt; 
  filter(`Significant-Strike-Attempt` &gt; 0) |&gt;
  summarise(avg_sig_attempt = mean(`Significant-Strike-Attempt`)) |&gt;
  mutate(FighterType = "Pre-McGregor UFC average")

mcgregor_sig &lt;- mcgregor_fights |&gt;
  filter(`Significant-Strike-Attempt` &gt; 0) |&gt;
  summarise(avg_sig_attempt = mean(`Significant-Strike-Attempt`)) |&gt;
  mutate(FighterType = "Conor McGregor")

sig_both &lt;- bind_rows(all_sig, mcgregor_sig)

bar_sig &lt;- ggplot() +
  geom_bar(data = sig_both,aes(x = reorder(FighterType, avg_sig_attempt), weight = avg_sig_attempt),fill = "black") +
  geom_bar(data = mcgregor_sig,aes(x = FighterType, weight = avg_sig_attempt),fill = "#169B62") +
  coord_flip() +
  labs(
    x = "",
    y = "Average significant strikes attempted"
  ) +
  theme_minimal() +
  theme(
    axis.title = element_text(size = 8),
    panel.grid.minor = element_blank()
  )

all_total &lt;- ufc |&gt;
  filter(FightYear &lt; 2014) |&gt;
  filter(`Total-Strikes-Attempt` &gt; 0) |&gt;
  summarise(avg_total_attempt = mean(`Total-Strikes-Attempt`)) |&gt;
  mutate(FighterType = "Pre-McGregor UFC average")

mcgregor_total &lt;- mcgregor_fights |&gt;
  filter(`Total-Strikes-Attempt` &gt; 0) |&gt;
  summarise(avg_total_attempt = mean(`Total-Strikes-Attempt`)) |&gt;
  mutate(FighterType = "Conor McGregor")

total_table &lt;- bind_rows(all_total, mcgregor_total)

bar_total &lt;- ggplot() +
  geom_bar(data = total_table,aes(x = reorder(FighterType, avg_total_attempt), weight = avg_total_attempt),fill = "black") +
  geom_bar(data = mcgregor_total,aes(x = FighterType, weight = avg_total_attempt),fill = "orange") +
  coord_flip() +
  labs(
    x = "",
    y = "Average total strikes attempted"
  ) +
  theme_minimal() +
  theme(
    axis.title = element_text(size = 8),
    panel.grid.minor = element_blank()
  )
bar_sig + bar_total +
  plot_annotation(
    title = "The UFC had never seen a fighter strike like McGregor",
    subtitle = "Comparing McGregor’s striking statistics to the UFC fighters before him.",
    caption = "Source: Kaggle (The Ultimate UFC Dataset by Prashanth Saravanan) | By Turner Ahrens"
  ) & 
  theme(
    plot.title = element_text(size = 16, face = "bold"),
    axis.title = element_text(size = 10), 
    plot.subtitle = element_text(size=10), 
    panel.grid.minor = element_blank(),
    plot.title.position = "plot"
    )
